import styled from "styled-components";

const SortStyled = styled.button`
  border: none;
  border-bottom: 1px solid #2f3640;
  padding: 5px;
  margin: 5px;

  ${(props) =>
    props.open &&
    css`
      background: #ff6b6b;
      &:hover {
        background: #ff8787;
      }
      &:active {
        background: #fa5252;
      }
      transform: translate(-50%, 50%) rotate(45deg);
    `}
`;

export default SortStyled;
