import axios from "axios";
// actiontypes
export const GET_SEARCH = "GET_SEARCH";
export const SET_INPUT = "SET_INPUT";

// action

export const setInput = (input = "") => async (dispatch) => {
  await dispatch({ type: SET_INPUT, payload: input });
};

export const getSearch = (keyword) => async (dispatch) => {
  const response = await axios.get(`api/v1/posts/${keyword}/search/`);
  if (response.data.length != 0) {
    dispatch({ type: GET_SEARCH, payload: response.data });
  } else {
    dispatch({ type: GET_SEARCH, payload: null });
  }
};

// initialState

const initialState = {
  results: [],
  input: "",
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case GET_SEARCH:
      return {
        ...state,
        results: action.payload,
      };
    case SET_INPUT:
      return {
        ...state,
        input: action.payload,
      };
    default:
      return state;
  }
}
