import axios from "axios";
// actiontypes
export const GET_SEARCH = "GET_SEARCH";

// action

export const getSearch = (keword) => async (dispatch) => {
  const response = await axios.get(`api/v1/posts/${input}/search`);
  dispatch({ type: GET_SEARCH, payload: response.data });
};
// initialState

const initialState = {
  results: [],
  input: "",
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case GET_SEARCH:
      return {
        ...state,
        results: action.payload,
      };
    default:
      return state;
  }
}
