import React, { useEffect, useState } from "react";
import SearchBoxStyled from "../styled/commonStyled/SearchBoxStyled";
import SearchStyled from "../styled/commonStyled/SearchStyled";
import { FaSearch } from "react-icons/fa";
import { Route, Routes, Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import getSearch from "../../redux/module/search";
import axios from "axios";

const Search = () => {
  const [keyword, setKeyword] = useState("");
  const handleKeyword = (e) => {
    setKeyword(e.target.value);
  };
  const handleSearch = async () => {
    const response = await axios.get(`/api/v1/posts/${keyword}/search`);
  };
  return (
    <SearchBoxStyled>
      <SearchStyled
        placeholder="Search"
        onChange={handleKeyword}
      ></SearchStyled>
      <FaSearch color="#7f8fa6" onClick={handleSearch} />
    </SearchBoxStyled>
  );
};

export default Search;
