import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchPost } from "../../redux/module/post";
import Post from "./Post";
import PostWrapperStyled from "../styled/mainStyled/PostWrapperStyled";

const Content = () => {
  const posts = useSelector((state) => state.post.posts);
  const [isRecent, setIsRecent] = useState(false);
  const dispatch = useDispatch();
  const postList = posts.map((post, index) => (
    <Post key={index} post={post}></Post>
  ));

  useEffect(() => {
    dispatch(fetchPost());
  }, []);

  const recentPost = posts.sort((a, b) => {
    let sortingField = "postsLike";
    return b[sortingField] - a[sortingField];
  });
  console.log(recentPost);

  return isRecent ? (
    <PostWrapperStyled>hello</PostWrapperStyled>
  ) : (
    <PostWrapperStyled>{postList}</PostWrapperStyled>
  );
};

export default Content;
