import styled from "styled-components";

const InputStyled = styled.input`
  width: 85%;
  height: 4%;
  margin-top: 1em;
  border: none;
  border-top: 1px solid black;

  &:focus {
    outline: none;
    transition: 500ms {
      font-size: 1em;
    }
  }
`;
export default InputStyled;
