import React, { useState, useEffect } from "react";
import SearchUserStyled from "../styled/searchStyled/SearchUserStyled";
import UserBlock from "../common/UserBlock";
import SearchResultWrapper from "../styled//searchStyled/SearchResultWrapper";
import { useDispatch, useSelector } from "react-redux";
import { getSearch } from "../../redux/module/search";
import Post from "../main/Post";

const SearchUser = () => {
  const [isExist, setIsExist] = useState(false);
  const keyword = useSelector((state) => state.search.input);
  const results = useSelector((state) => state.search.results);
  const dispatch = useDispatch();

  useEffect(async () => {
    await dispatch(getSearch(keyword));
    if (results.length == 0) {
      setIsExist(false);
    } else {
      setIsExist(true);
    }
  }, [results]);

  const resultList = results.map((post, index) => (
    <Post key={index} post={post}></Post>
  ));
  return (
    <SearchUserStyled>
      <p>User</p>
      <SearchResultWrapper>
        <UserBlock />
        <UserBlock />
        <UserBlock />
      </SearchResultWrapper>
    </SearchUserStyled>
  );
};

export default SearchUser;
