import React, { useState, useEffect } from "react";
import PostStyled from "../styled/mainStyled/PostStyled";
import VideoWrapper from "../styled/mainStyled/VideoWrapper";
import UserBlock from "../common/UserBlock";
import VideoModal from "../videoModal/VideoModal";
import Modal from "react-modal";
import { useSelector, useDispatch } from "react-redux";
import { MdFavorite } from "react-icons/md";
import { MdPlayArrow } from "react-icons/md";
import { toggleModal } from "../../redux/module/post";
import ContentInfo from "../videoModal/ContentInfo";
import axios from "axios";

const Post = ({ post }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [detailProps, setDetailProps] = useState({});
  const toggleModal = () => {
    /* dispatch로 코드 리팩토링 해야하는 부분*/
    setIsModalOpen(!isModalOpen);
    if (!isModalOpen) {
      axios
        .get(`http://localhost:8080/api/v1/posts/${post.postsId}/detail`)
        .then((res) => {
          setDetailProps(res);
        });
    }
  };
  console.log(detailProps);
  return (
    <>
      <PostStyled onClick={toggleModal}>
        <img
          width="100%"
          height="80%"
          src={`http://localhost:8080/api/v1/posts/thumbnail/${post.thumbnailName}`}
        />
        <UserBlock author={detailProps} />
        <MdFavorite /> :{post.postsLike}
        <MdPlayArrow /> : {post.views}
      </PostStyled>
      <VideoModal
        isOpened={isModalOpen}
        onClose={toggleModal}
        videoSrc={post.thumbnailName}
        detail={post.postsID}
      ></VideoModal>
    </>
  );
};

export default Post;
