import React, { useEffect } from "react";
import SideWrapper from "../styled/modalStyled/SideWrapper";
import UserBlcok from "../common/UserBlock";
import VideoDescription from "./VideoDescription";
import ContentInfo from "./ContentInfo";
import CommentList from "./CommentList";
import { Link } from "react-router-dom";
import CommentInput from "./CommentInput";
import UserBlock from "../common/UserBlock";
import UserProfileStyled from "../styled/commonStyled/UserProfileStyled";
import UserBlockStlyed from "../styled/commonStyled/UserBlockStyled";
import { useSelector, useDispatch } from "react-redux";

import CommentListWrapper from "../styled/modalStyled/CommentListWrapper";
import { fetchComments } from "../../redux/module/comment";

const SideContent = ({ isOpened }) => {
  const postDetail = useSelector((state) => state.post.postDetail);
  const comments = useSelector((state) => state.comment.comments);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchComments(`${postDetail.postsId}`));
  }, []);

  const commentList = comments.map((comment, index) => (
    <Comment key={index} comment={comment}></Comment>
  ));

  return (
    <SideWrapper>
      <div>
        {isOpened && (
          <UserBlockStlyed>
            <UserProfileStyled
              src="../../public/logo192.png"
              width="32px"
              height="32px"
            />
            <p>{postDetail.authorName}</p>
          </UserBlockStlyed>
        )}
      </div>
      <VideoDescription />
      <ContentInfo />
      <CommentListWrapper>{commentList}</CommentListWrapper>
      <CommentInput />
    </SideWrapper>
  );
};

export default SideContent;
