// action;
const TOGGLE_MODAL = "TOGGLE_MODAL";

export const toggleModal = (isOpened) => {
  return { type: TOGGLE_MODAL };
};

// reducer
const initialState = {
  isOpened: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_MODAL:
      return {
        ...state,
        isOpened: !state.isOpened,
      };
    default:
      return state;
  }
}
