import React, { useState, useEffect, useLayoutEffect } from "react";
import SideNav from "../common/SideNav";
import ChannelWrapperStyled from "../styled/channelStyled/ChannelWrapperStyled";
import ChannelContentWrapper from "../styled/channelStyled/ChannelContentWrapper";
import ChannelInfoWrapper from "./ChannelInfoWrapper";
import Content from "../main/Content";
import ContentWrapper from "../styled/mainStyled/ContentWrapper";
import { useMediaQuery } from "react-responsive";
import { useDispatch, useSelector } from "react-redux";
import { fetchChannel } from "../../redux/module/channel";

const Channel = () => {
  const post = useSelector((state) => state.post.postDetail);
  const channelData = useSelector((state) => state.channel.channelData);
  const userId = post.authorId;
  const dispatch = useDispatch();
  const isMobile = useMediaQuery({
    query: "(max-width:1024px)",
  });

  const fetchMyPost = () => {
    if (post.authorId) {
      dispatch(fetchChannel(post.authorId));
      console.log(channelData);
    } else {
      return null;
    }
  };

  document.body.style.overflow = "auto";
  return (
    <ChannelWrapperStyled>
      {isMobile ? null : <SideNav />}
      <ContentWrapper>
        <ChannelInfoWrapper />
        {fetchMyPost()}
      </ContentWrapper>
    </ChannelWrapperStyled>
  );
};

export default Channel;
