import React, { useState } from "react";
import PostStyled from "../styled/mainStyled/PostStyled";
import VideoWrapper from "../styled/mainStyled/VideoWrapper";
import UserBlock from "../common/UserBlock";
import VideoModal from "../videoModal/VideoModal";
import Modal from "react-modal";
import { useSelector, useDispatch } from "react-redux";
import { MdFavorite } from "react-icons/md";
import { toggleModal } from "../../redux/module/post";
import ContentInfo from "../videoModal/ContentInfo";
const Post = ({ post }) => {
  const firstOpen = false;
  const isOpened = useSelector((state) => state.post.isOpened);
  const dispatch = useDispatch();

  return (
    <PostStyled>
      <VideoWrapper />
      <UserBlock />
      <MdFavorite />:{post.postsLike}
      views : {post.views}
      <ContentInfo />
      <VideoModal
        isOpened={isOpened}
        onClick={() => dispatch(toggleModal(isOpened))}
      ></VideoModal>
    </PostStyled>
  );
};

export default Post;
