import styled from "styled-components";

const DropDown = styled.div`
  display: ${(props) => (props.display ? "block" : "none")};
  flex-direction: column;
  justify-content: space-between
  align-items: center;
  position: absolute;
  z-index: 5;
  background-color:red;
  border-radius: 4px;
  margin-top:4px;
`;

export default DropDown;
