import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchPost, fetchPostPopular } from "../../redux/module/post";
import Post from "./Post";
import Loader from "./Loader";
import TargetStyled from "../styled/mainStyled/TargetStyled";
import PostWrapperStyled from "../styled/mainStyled/PostWrapperStyled";
import axios from "axios";

const Content = () => {
  const [target, setTarget] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [itemLists, setItemLists] = useState([]);
  const [isEnd, setIsEnd] = useState(false);
  const [lastId, setLastId] = useState("");
  const [pageNum, setPageNum] = useState(0);
  const dispatch = useDispatch();
  const posts = useSelector((state) => state.post.posts);

  const getMoreItem = async (items) => {
    setIsLoaded(true);
    await new Promise((resolve) => setTimeout(resolve, 500));
    setItemLists((itemLists) => itemLists.concat(items));
    setIsLoaded(false);
  };

  const onIntersect = async ([entry], observer) => {
    if (entry.isIntersecting && !isLoaded) {
      observer.unobserve(entry.target);
      try {
        const response = await axios.get(`api/v1/posts/${pageNum}/popular`);
        let items = response.data;
        await getMoreItem(items);
      } catch {
        setIsEnd(true);
      }
      observer.observe(entry.target);
    }
  };

  useEffect(async () => {
    let observer;
    if (target && !isLoaded) {
      observer = new IntersectionObserver(onIntersect, {
        threshold: 0.5,
      });
      observer.observe(target);
    }
    return () => observer && observer.disconnect();
  }, [target]);

  return (
    <>
      <PostWrapperStyled>
        {itemLists.map((post, i) => {
          return <Post post={post} key={i} />;
        })}
      </PostWrapperStyled>
      {isEnd && <div>마지막 게시물입니다.</div>}
      {!isEnd && (
        <TargetStyled ref={setTarget}>{isLoaded && <Loader />}</TargetStyled>
      )}
    </>
  );
};

export default Content;
