import React from "react";
import ContentInfoWrapper from "../styled/modalStyled/ContentInfoWrapper";
import Like from "./Like";
import { MdSms } from "react-icons/md";
import { useSelector } from "react-redux";
import Cookies from "universal-cookie";
import {
  requestLike,
  requestLikeCancel,
  fetchLike,
} from "../../redux/module/like";

const ContentInfo = () => {
  const postDetail = useSelector((state) => state.post.postDetail);
  const dispatch = useDispatch();
  const cookies = new Cookies();
  const user = cookies.get("user");

  useEffect(() => {
    dispatch(fetchLike(user.userId, postsId));
  }, [postsId]);

  return (
    <ContentInfoWrapper>
      <Like />
      Like: {postDetail.postsLike}
      <MdSms />
      Comments: {postDetail.totalCommentsCount}
      Views : {postDetail.views}
    </ContentInfoWrapper>
  );
};

export default ContentInfo;
