import React from "react";

const EditButtons = ({ target }) => {
  const handleDelete = () => {
    switch (target) {
      case "post":
        return console.log("postedelete");
    }
  };
  const handleEdit = () => {
    switch (target) {
      case "comment":
        return console.log("commentedit");
    }
  };

  return (
    <div
      style={{
        display: "flex",
      }}
    >
      <button
        onClick={handleEdit}
        style={{
          border: "none",
          color: "#2f3640",
          backgroundColor: "white",
        }}
      >
        {target} 수정
      </button>
      <button
        onClick={handleDelete}
        style={{
          border: "none",
          color: "#e84118",
          backgroundColor: "white",
        }}
      >
        {target} 삭제
      </button>
    </div>
  );
};

export default EditButtons;
