import React, { useEffect, useState, useMemo } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchPost } from "../../redux/module/post";
import Post from "../main/Post";
import Loader from "../main/Loader";
import axios from "axios";
import { throttle } from "lodash";
import PostWrapperStyled from "../styled/mainStyled/PostWrapperStyled";
import TargetStyled from "../styled/mainStyled/TargetStyled";

const RecentContent = () => {
  const posts = useSelector((state) => state.post.posts);
  const dispatch = useDispatch();

  const handleScroll = () => {
    if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
      dispatch(fetchPost());
      console.log(posts);
    } else {
      console.log("not end");
    }
  };
  const infiniteScroll = throttle(handleScroll, 1000);

  useEffect(() => {
    window.addEventListener("scroll", infiniteScroll);
    return () => {
      window.removeEventListener("scroll", infiniteScroll); //clean up
    };
  }, []);
  return (
    <>
      <PostWrapperStyled>
        {posts.map((post, i) => {
          return <Post post={post} key={i} />;
        })}
      </PostWrapperStyled>
    </>
  );
};

export default RecentContent;
