import styled from "styled-components";

const CommentDropDown = styled.div`
  visibility: ${(props) => (props.visible ? "visible" : "hidden")};

  justify-content: space-around;
  z-index: 5;

  background-color: white;
  border-radius: 4px;

  width: 6em;
`;

export default CommentDropDown;
