import React from "react";
import SortStyled from "../styled/mainStyled/SortStyled";
import ButtonStyled from "../styled/commonStyled/ButtonStyled";
import { MdTrendingUp } from "react-icons/md";
import { MdAvTimer } from "react-icons/md";
import { Route, Routes, NavLink } from "react-router-dom";
import Main from "./Main";
import Recent from "../recent/Recent";
import { useDispatch } from "react-redux";
import { fetchPost, fetchPostPopular } from "../../redux/module/post";

const navLinkStyled = ({ isActive }) => {
  return {
    borderBottom: isActive ? "2px solid black" : "white",
  };
};

const handleClick = (e) => {
  if (e.target.dataset.name !== "popular") {
    console.log(e.target.dataset.name, " clciked");
  } else {
    console.log(e.target.dataset.name, " clciked");
  }
};
const Sort = () => {
  return (
    <SortStyled>
      <NavLink
        style={navLinkStyled}
        to="/"
        onClick={handleClick}
        data-name="popular"
      >
        <ButtonStyled>
          <MdTrendingUp />
          인기
        </ButtonStyled>
      </NavLink>
      <NavLink
        style={navLinkStyled}
        to="/recent"
        onClick={handleClick}
        data-name="recent"
      >
        <ButtonStyled>
          <MdAvTimer />
          최신
        </ButtonStyled>
      </NavLink>
    </SortStyled>
  );
};

export default Sort;
