import { useSelector, useDispatch } from "react-redux";
import { fetchComments } from "../../redux/module/comment";

const CommentList = () => {
  const postDetail = useSelector((state) => state.post.postDetail);
  const comments = useSelector((state) => state.comment.comments);
  const dispatch = useDispatch();
  console.log(postDetail.postId);
  useEffect(async () => {
    let comments = await dispatch(fetchComments(`${postId}`));
  }, []);
  console.log("from commentList", comments);
  return <CommentListWrapper></CommentListWrapper>;
};

export default CommentList;
