import styled from "styled-components";

const SortStyled = styled.button`
  display: inline-block;
  border: none;
  padding: 1em;
  &:hover {
    color: #dcdde1;
    border-bottom: 1px solid #2f3640;
  }
`;

export default SortStyled;
