import axios from "axios";
// actiontypes
export const GET_SEARCH = "GET_SEARCH";
export const SET_INPUT = "SET_INPUT";

// action

export const setInput = (useEffect) => ({ type: SET_INPUT, payload: input });

// initialState

const initialState = {
  results: [],
  input: "",
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case GET_SEARCH:
      return {
        ...state,
        results: action.payload,
      };
    case SET_INPUT:
      return {
        ...state,
        input: action.payload,
      };
    default:
      return state;
  }
}
