import React from "react";
import UserProfileStyled from "../styled/commonStyled/UserProfileStyled";
import UserBlockStlyed from "../styled/commonStyled/UserBlockStyled";
import Cookies from "universal-cookie";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchChannel } from "../../redux/module/channel";

const UserBlock = ({ userName, src, children, userId, author }) => {
  const dispatch = useDispatch();
  console.log(userId);
  const handleClick = () => {
    dispatch(fetchChannel(userId));
  };
  return (
    <Link to={`/channel/${author}/`} onClick={handleClick}>
      <UserBlockStlyed>
        <UserProfileStyled src={src} width="32px" height="32px" />
        {children}
      </UserBlockStlyed>
    </Link>
  );
};

export default UserBlock;
