import axios from "axios";

// actiontypes
const FETCH_RECOMMAND_USERS = "FETCH_RECOMMAND_USERS";
const FETCH_RECOMMAND_TAGS = "FETCH_RECOMMAND_TAGS";

// action

export const fetchRecommandUsers = () => async (dispatch) => {
  const response = await axios.get(`api/v1/recommend/user`);
  dispatch({ type: FETCH_RECOMMAND_USERS, payload: response.data });
};

const initialState = {
  recommandUsers: [],
  recommandTags: [],
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_RECOMMAND_USERS:
      return {
        ...state,
        recommendUser: action.payload,
      };
    default:
      return state;
  }
}
