import React, { useState } from "react";
import ButtonWrapper from "./styled/uploadStyled/ButtonWrapper";
import UploadButtonStyled from "./styled/uploadStyled/UploadButtonStyled";
import UploadWrapper from "./styled/uploadStyled/UploadWrapper";
import InputWrapper from "./styled/uploadStyled/InputWrapper";
import DescriptionInput from "./styled/uploadStyled/DescriptionInput";
import CaptionInput from "./styled/uploadStyled/CaptionInput";
import PostForm from "./upload/PostForm";
import UploaderStyled from "./styled/uploadStyled/UploaderStyled";
import PostFormStyled from "./styled/uploadStyled/PostFormStyled";
import FormStyled from "./styled/uploadStyled/FormStyled";
import { useNavigate } from "react-router-dom";

import axios from "axios";
const UploadTest = () => {
  const formData = new FormData();
  const config = {
    header: { "content-type": "multipart/form-data" },
  };

  const handleFileChange = (e) => {
    if (e.target && e.target.files[0]) {
      formData.append("file", e.target.files[0]);
    }
  };

  const handleUserId = (e) => {
    formData.append("userId", e.target.value);
  };

  const handleTags = (e) => {
    formData.append("tags", e.target.value);
  };

  const handleDescription = (e) => {
    formData.append("description", e.target.value);
  };

  const handleSubmit = () => {
    axios
      .post("http://localhost:8080/api/v1/posts", { formData }, { config })
      .then((res) => console.log(res));
  };

  return (
    <>
      <UploadWrapper>
        <FormStyled>
          <PostFormStyled>
            <h3>Upload Your Days</h3>
            <UploaderStyled>
              <input
                multiple
                type="file"
                name="file"
                onChange={handleFileChange}
              />
            </UploaderStyled>
          </PostFormStyled>
          <InputWrapper>
            <input
              type="text"
              name="userId"
              placeholder="userid"
              onChange={handleUserId}
            />
            <CaptionInput
              type="text"
              name="tags"
              placeholder="Input Tags"
              onChange={handleTags}
            />
            <DescriptionInput
              type="text"
              name="description"
              placeholder="Description"
              onChange={handleDescription}
            />
            <UploadButtonStyled type="submit" onSubmit={handleSubmit}>
              upload
            </UploadButtonStyled>
            <ButtonWrapper></ButtonWrapper>
          </InputWrapper>
        </FormStyled>
      </UploadWrapper>
    </>
  );
};

export default UploadTest;
