import React, { useEffect } from "react";
import Loader from "../main/Loader";
import LoadingWrapper from "../styled/commonStyled/LoadingWrapper";
import { useDispatch, useSelector } from "react-redux";
import { getCode, loginRequest } from "../../redux/module/login";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import axios from "axios";

const Loading = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const code = useSelector((state) => state.login.code);
  const path = useSelector((state) => state.login.path);
  const [cookie, setCookie] = useCookies(["userData"]);
  useEffect(async () => {
    const authCode = new URL(window.location.href).searchParams.get("code");
    console.log(path);
    const response = await axios.get(`http://localhost:8080/api/v1/jwt/naver`, {
      params: {
        code: authCode,
      },
    });
    dispatch(loginRequest(response.data));
    setCookies;
    navigate("/");
  }, []);

  return (
    <LoadingWrapper>
      <Loader />
      <div>잠시만 기다려주세요...</div>
    </LoadingWrapper>
  );
};

export default Loading;
