// actiontype
const TOGGLE_MODAL = "TOGGLE_MODAL";

//action

export const toggleModal = (isModalOpened) => (dispatch) => {
  dispatch({ type: TOGGLE_MODAL, payload: isModalOpened });
};

// initialState

const initialState = {
  isModalOpened: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_MODAL:
      return {
        ...state,
        isModalOpened: !action.payload,
      };
  }
}
