import React, { useState } from "react";
import TagWrapper from "../styled/commonStyled/TagWrapper";
import { useSelector, useDispatch } from "react-redux";
import { setInput } from "../../redux/module/search";
import { useNavigate } from "react-router-dom";

const Tag = ({ children }) => {
  const [input, setInput] = useState("");
  const keyword = useSelector((state) => state.search.input);
  const [tag, setTag] = useState(keyword);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleClick = (e) => {
    setInput(e.target.innerText);
    dispatch(setInput(e.target.innerText));
    navigate("/search");
  };
  return (
    <div>
      <TagWrapper onClick={handleClick}>{children}</TagWrapper>
    </div>
  );
};

export default Tag;
