import axios from "axios";

//actiontypes

const FETCH_CHANNEL = "FETCH_CHANNEL";

//action

export const fetchChannel = (userId) => async (dispatch) => {
  const response = await axios.get(
    `http://localhost:8080/api/v1/user/${userId}`
  );
  dispatch({ type: FETCH_CHANNEL, payload: response.data });
};

export const userFetchPost = (userId) => async (dispatch) => {
  const response = await axios.get(`/api/v1/user/${userId}/posts`);
};
//initialState
const initialState = {
  channelData: {},
  myPost: [],
};

//reducer

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_CHANNEL:
      return {
        ...state,
        channelData: action.payload,
      };
    default:
      return state;
  }
}
